import React, { useEffect, useState } from "react";
import axios from "axios";
import config from "../config";
import { useNavigate, useParams } from "react-router-dom";

const BlogDetail = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [inputs, setInputs] = useState({ title: "", description: "" });

  useEffect(() => {
    axios
      .get(`${config.BASE_URL}/api/blogs/${id}`)
      .then((res) => {
        setBlog(res.data.blog);
        setInputs({
          title: res.data.blog.title,
          description: res.data.blog.description,
        });
      })
      .catch(console.error);
  }, [id]);

  const handleChange = (e) =>
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .put(`${config.BASE_URL}/api/blogs/update/${id}`, inputs)
      .then(() => navigate("/myBlogs"));
  };

  if (!blog) {
    return <p className="text-center mt-8">Loading blog details...</p>;
  }

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white shadow-lg rounded-lg mt-6">
      <h2 className="text-2xl font-bold text-gray-800 mb-4">Update Blog</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="title"
          value={inputs.title}
          onChange={handleChange}
          required
          className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-indigo-500"
        />
        <textarea
          name="description"
          value={inputs.description}
          onChange={handleChange}
          rows="6"
          required
          className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-indigo-500 resize-none"
        />
        <button
          type="submit"
          className="w-full py-2 bg-indigo-600 hover:bg-indigo-700 text-white font-semibold rounded-md transition"
        >
          Update
        </button>
      </form>
    </div>
  );
};

export default BlogDetail;
