import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { authActions, setDarkmode } from "../store";
import {
  AppBar,
  Typography,
  Toolbar,
  Box,
  Button,
  Tabs,
  Tab,
} from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import DarkModeIcon from "@mui/icons-material/DarkMode";
import LightModeIcon from "@mui/icons-material/LightMode";
import { lightTheme, darkTheme } from "../utils/theme";

const Header = () => {
  const dispatch = useDispatch();
  const isDark = useSelector((state) => state.theme.isDarkmode);
  const theme = isDark ? darkTheme : lightTheme;
  const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);

  const [value, setValue] = useState();
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const savedTab = localStorage.getItem("selectedTab");
    const savedTheme = localStorage.getItem("isDarkMode");
    if (savedTab !== null) {
      setValue(parseInt(savedTab, 10));
    }
    if (savedTheme !== null) {
      dispatch(setDarkmode(JSON.parse(savedTheme)));
    }
  }, []);

  useEffect(() => {
    const path = location.pathname;
    if (path.startsWith("/blogs/add")) {
      setValue(2);
    } else if (path.startsWith("/myBlogs")) {
      setValue(1);
    } else if (path.startsWith("/blogs")) {
      setValue(0);
    } else {
      setValue(0);
    }
  }, [location.pathname]);

  const handleTabChange = (e, newValue) => {
    setValue(newValue);
    localStorage.setItem("selectedTab", newValue);
  };

  const handleDarkModeToggle = () => {
    const newTheme = !isDark;
    localStorage.setItem("isDarkMode", newTheme);
    dispatch(setDarkmode(newTheme));
  };

  const handleLoginClick = () => {
    navigate("/login", { state: { isSignupButtonPressed: false } });
  };

  const handleSignupClick = () => {
    navigate("/login", { state: { isSignupButtonPressed: true } });
  };

  return (
    <AppBar
      position="sticky"
      sx={{
        background: "linear-gradient(to right, #0f2027, #203a43, #2c5364)",
        boxShadow: "0 4px 12px rgba(0, 0, 0, 0.3)",
        paddingX: 2,
      }}
    >
      <Toolbar>
        <Typography
          variant="h4"
          sx={{
            fontWeight: "bold",
            fontFamily: "Poppins, sans-serif",
            color: "#ffffff",
          }}
        >
          Blogeey
        </Typography>

        {isLoggedIn && (
          <Box display="flex" marginLeft="auto" marginRight="auto">
            <Tabs
              textColor="inherit"
              value={value}
              onChange={handleTabChange}
              TabIndicatorProps={{
                style: {
                  backgroundColor: "#00d1b2",
                  height: 3,
                },
              }}
              sx={{
                ".MuiTab-root": {
                  color: "#dcdcdc",
                  fontWeight: 600,
                  fontSize: "15px",
                  fontFamily: "Poppins, sans-serif",
                  textTransform: "uppercase",
                  marginX: 1.5,
                },
                ".Mui-selected": {
                  color: "#00d1b2 !important",
                },
              }}
            >
              <Tab LinkComponent={Link} to="/blogs" label="All Blogs" />
              <Tab LinkComponent={Link} to="/myBlogs" label="My Blogs" />
              <Tab LinkComponent={Link} to="/blogs/add" label="Add Blog" />
            </Tabs>
          </Box>
        )}

        <Box display="flex" alignItems="center" marginLeft="auto">
          {!isLoggedIn && (
            <>
              <Button
                onClick={handleLoginClick}
                sx={{
                  margin: 1,
                  fontWeight: "bold",
                  color: "white",
                  backgroundColor: "#00c853",
                  borderRadius: "20px",
                  "&:hover": {
                    backgroundColor: "#00b342",
                  },
                }}
              >
                Login
              </Button>
              <Button
                onClick={handleSignupClick}
                sx={{
                  margin: 1,
                  fontWeight: "bold",
                  color: "white",
                  backgroundColor: "#2962ff",
                  borderRadius: "20px",
                  "&:hover": {
                    backgroundColor: "#0039cb",
                  },
                }}
              >
                SignUp
              </Button>
            </>
          )}

          {isLoggedIn && (
            <Button
              onClick={() => dispatch(authActions.logout())}
              LinkComponent={Link}
              to="/login"
              variant="contained"
              sx={{
                margin: 1,
                borderRadius: "20px",
                backgroundColor: "#f57c00",
                "&:hover": {
                  backgroundColor: "#ef6c00",
                },
              }}
            >
              Logout
            </Button>
          )}

          <Box
            onClick={handleDarkModeToggle}
            sx={{
              padding: "10px",
              marginLeft: 1,
              cursor: "pointer",
              display: "flex",
              alignItems: "center",
              color: "white",
              "&:hover": {
                color: "#00d1b2",
              },
            }}
          >
            {isDark ? <LightModeIcon /> : <DarkModeIcon />}
          </Box>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
